-- ----------------------------
-- Table structure for categories
-- ----------------------------
DROP TABLE IF EXISTS `categories`;
CREATE TABLE `categories`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `description` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `thumbnail` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `parent_id` int NULL DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 14 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of categories
-- ----------------------------
INSERT INTO `categories` VALUES (1, 'Giống chó', 'Giống chó là nhóm các con chó nhà có liên quan chặt chẽ và tương tự nhau, đó là tất cả các loài có những đặc điểm đặc trưng được lựa chọn và duy trì bởi con người. Các giống chó cũng là dùng để chỉ giống tự nhiên hoặc các giống mà phát sinh thông qua thời gian để đáp ứng với một môi trường cụ thể trong đó có con người, với ít hoặc không có giống chọn lọc của con người.', 'cho.jpg', 0, '2022-01-16 00:03:00');
INSERT INTO `categories` VALUES (2, 'Thức Ăn', 'Thức ăn cho chó Có hai loại chính: thực phẩm tươi và thực phẩm khô.', 'thuc_an_cho_cho.jpg', 0, '2022-01-11 02:00:00');
INSERT INTO `categories` VALUES (3, 'Lợi ích', 'Nuôi chó lợi ích gì? Tại sao chúng ta nên nuôi chó thay vì một con vật khác. Những chú chó mang lại rất nhiều lợi ích cho con người. Chúng được huấn luyện để hỗ trợ cho con người. Là một người bạn dẫn đường nếu như đôi mắt chẳng con tinh tường. Những chú chó nghiệp vụ đánh hơi tìm kiếm tội phạm. Hay đơn giản chỉ là một người bạn tâm tình mỗi khi ta buồn. Chó là loài vật rất xứng đáng được yêu thương và trân trọng. Qua bài viết này, bác sĩ thú y sẽ cung cấp thêm cho bạn rất nhiều thông tin hữu ích tới việc nuôi thú cưng , đặc biệt là về những chú chó.', 'loi-ich-khi-nuoi-cho.jpg', 0, '2022-01-10 23:30:00');
INSERT INTO `categories` VALUES (4, 'Sức khỏe', 'Khi bạn mang một chú cún con về nhà, bạn sẽ chẳng muốn làm gì hơn ngoài …tắm cho chú trong tình cảm, lòng yêu thương. Nhưng quả bóng nhỏ thực tế dễ bị bệnh hơn những con chó trưởng thành có hệ thống miễn dịch đã đầy đủ. Là một chủ sở hữu mới, bạn có thể chưa biết các dấu hiệu cho thấy chú cún bị bệnh. Những chú chó có vẻ khá đỏng đảnh khi mắc bệnh và chết mà chủ nhân không biết lý do. Cùng tham khảo một vài bệnh thường gặp dưới đây để chăm sóc những chú cún cưng của bạn thật tốt nhé!', 'suc_khoe_cho.jpg', 0, '2022-01-11 02:01:00');
INSERT INTO `categories` VALUES (5, 'Giá tiền', 'Xin chào các bạn, nhiều bạn đang thắc mắc về giá chó cảnh hiện nay là bao nhiều một chú, và giá từng giống chó cảnh ở Việt Nam bây giờ thế nào, để cho các bạn nắm vững trước khi đi mua để tránh bị “xỏ mũi” thì mình xin giới thiệu bảng giá chó cảnh hiện nay được cập nhật mới nhất, với mức giá trung bình của từng loại chó cảnh giúp các bạn dễ dàng lựa chọn cún cho mình.', 'gia ca.jpg', 0, '2022-01-10 23:40:00');
INSERT INTO `categories` VALUES (6, 'Chó thông minh', 'hó được xem là một trong số những loài động vật thông minh và tình cảm nhất thế giới. Chúng khả năng cảm nhận được lời nói và cảm xúc của con người. Chính vì đặc tính thông minh, trung thành và thân thiện, những chú chó đã trở thành những người bạn đồng hành ở đời sống của con người.', 'top cho.jpg', 0, '2022-01-11 02:05:00');

-- ----------------------------
-- Table structure for posts
-- ----------------------------
DROP TABLE IF EXISTS `posts`;
CREATE TABLE `posts`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `description` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `thumbnail` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `category_id` int NULL DEFAULT NULL,
  `content` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `like_post` int NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 22 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

